import React, { useState, useEffect } from 'react';
import { 
  Box, 
  Button, 
  TextField, 
  FormControl, 
  InputLabel, 
  Select, 
  MenuItem, 
  Dialog, 
  DialogActions, 
  DialogContent, 
  DialogTitle,
  SelectChangeEvent
} from '@mui/material';
import { useExpense } from '../../contexts/ExpenseContext';
import { Expense, ExpenseCategory } from '../../types/expense';

interface ExpenseFormProps {
  open: boolean;
  onClose: () => void;
  expense?: Expense;
  mode: 'add' | 'edit';
}

const EXPENSE_CATEGORIES: ExpenseCategory[] = [
  'Food & Dining',
  'Transportation',
  'Utilities',
  'Housing',
  'Entertainment',
  'Healthcare',
  'Shopping',
  'Personal Care',
  'Education',
  'Travel',
  'Other'
];

const ExpenseForm: React.FC<ExpenseFormProps> = ({ open, onClose, expense, mode }) => {
  const { dispatch } = useExpense();
  const [formData, setFormData] = useState({
    amount: 0,
    description: '',
    category: 'Other' as ExpenseCategory,
    date: new Date().toISOString().split('T')[0]
  });
  const [errors, setErrors] = useState({
    amount: false,
    description: false,
    date: false
  });

  useEffect(() => {
    if (expense && mode === 'edit') {
      setFormData({
        amount: expense.amount,
        description: expense.description,
        category: expense.category,
        date: new Date(expense.date).toISOString().split('T')[0]
      });
    } else {
      // Reset form for add mode
      setFormData({
        amount: 0,
        description: '',
        category: 'Other' as ExpenseCategory,
        date: new Date().toISOString().split('T')[0]
      });
    }
  }, [expense, mode, open]);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    if (name) {
      setFormData({
        ...formData,
        [name]: value
      });

      if (name in errors) {
        setErrors({
          ...errors,
          [name]: false
        });
      }
    }
  };

  const handleSelectChange = (e: SelectChangeEvent<ExpenseCategory>) => {
    const { name, value } = e.target;
    if (name) {
      setFormData({
        ...formData,
        [name]: value as ExpenseCategory
      });
    }
  };

  const validateForm = (): boolean => {
    const newErrors = {
      amount: formData.amount <= 0,
      description: formData.description.trim() === '',
      date: !formData.date
    };

    setErrors(newErrors);
    return !Object.values(newErrors).some(error => error);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    if (mode === 'add') {
      dispatch({
        type: 'ADD_EXPENSE',
        payload: {
          ...formData,
          amount: Number(formData.amount),
          date: new Date(formData.date).toISOString()
        }
      });
    } else if (mode === 'edit' && expense) {
      dispatch({
        type: 'EDIT_EXPENSE',
        payload: {
          ...expense,
          ...formData,
          amount: Number(formData.amount),
          date: new Date(formData.date).toISOString()
        }
      });
    }

    onClose();
  };

  return (
    <Dialog open={open} onClose={onClose} fullWidth maxWidth="sm">
      <DialogTitle>{mode === 'add' ? 'Add New Expense' : 'Edit Expense'}</DialogTitle>
      <DialogContent>
        <Box component="form" noValidate sx={{ mt: 1 }}>
          <TextField
            margin="normal"
            required
            fullWidth
            id="description"
            label="Description"
            name="description"
            value={formData.description}
            onChange={handleInputChange}
            error={errors.description}
            helperText={errors.description ? 'Description is required' : ''}
            autoFocus
          />

          <TextField
            margin="normal"
            required
            fullWidth
            id="amount"
            label="Amount"
            name="amount"
            type="number"
            value={formData.amount}
            onChange={handleInputChange}
            error={errors.amount}
            helperText={errors.amount ? 'Amount must be greater than 0' : ''}
            InputProps={{ inputProps: { min: 0, step: 0.01 } }}
          />

          <FormControl fullWidth margin="normal">
            <InputLabel id="category-label">Category</InputLabel>
            <Select
              labelId="category-label"
              id="category"
              name="category"
              value={formData.category}
              label="Category"
              onChange={handleSelectChange}
            >
              {EXPENSE_CATEGORIES.map(category => (
                <MenuItem key={category} value={category}>
                  {category}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            margin="normal"
            required
            fullWidth
            id="date"
            label="Date"
            name="date"
            type="date"
            value={formData.date}
            onChange={handleInputChange}
            error={errors.date}
            helperText={errors.date ? 'Date is required' : ''}
            InputLabelProps={{ shrink: true }}
          />
        </Box>
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose}>Cancel</Button>
        <Button onClick={handleSubmit} variant="contained" color="primary">
          {mode === 'add' ? 'Add Expense' : 'Save Changes'}
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default ExpenseForm;
